import React, { useState } from "react";
import logo from "../Images/food.png";
import admin from "../Images/Admin.png";
import axios from "axios";
import { toast } from "react-toastify";
import { Link, useNavigate } from "react-router-dom";

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
        // ‡πÄ‡∏£‡∏µ‡∏¢‡∏Å API ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö
        const response = await axios.post("http://localhost:5293/api/Auth/login", {
            email,
            password,
        });

        // ‡πÅ‡∏™‡∏î‡∏á Token ‡πÉ‡∏ô Console (‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡πÇ‡∏´‡∏°‡∏î‡∏û‡∏±‡∏í‡∏ô‡∏≤)
        if (process.env.NODE_ENV === "development") {
            console.log("JWT Token:", response.data.token);
        }

        // ‡πÄ‡∏Å‡πá‡∏ö JWT Token ‡πÉ‡∏ô LocalStorage
        localStorage.setItem("authToken", response.data.token);

        // ‡πÅ‡∏à‡πâ‡∏á‡πÄ‡∏ï‡∏∑‡∏≠‡∏ô‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à
        toast.success("Login successful! üéâ");

        // ‡∏ô‡∏≥‡∏ó‡∏≤‡∏á‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ Dashboard ‡∏´‡∏£‡∏∑‡∏≠ Sale
        navigate("/select-branch");
        } catch (error) {
        console.error(error.response?.data || error.message);
        toast.error(
            error.response?.data?.message || "Unable to login. Please try again."
        );
        } finally {
        setIsLoading(false);
        }
    };

    return (
        <div className="login-register-container">
        <div className="logo">
            <img src={logo} alt="App Logo" />
        </div>
        <div className="form-container">
            <img src={admin} className="avatar" alt="Admin Avatar" />
            <form onSubmit={handleLogin}>
            <input
                type="text-form"
                placeholder="Email..."
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
            />
            <div style={{ position: "relative" }}>
                <input
                    type={showPassword ? "text" : "password"} // ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô type ‡∏ï‡∏≤‡∏°‡∏Ñ‡πà‡∏≤ showPassword
                    placeholder="Password..."
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    style={{ paddingRight: "40px" }}
                />
                <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)} // toggle state
                    style={{
                    position: "absolute",
                    right: "10px",
                    top: "50%",
                    transform: "translateY(-50%)",
                    }}
                >
                    {showPassword ? "Hide" : "Show"}
                </button>
            </div>
            <button type="submit" className="btn" disabled={isLoading}>
                {isLoading ? "Processing..." : "Login"}
            </button>
            </form>
            <p>
            Don't have an account? <Link to="/register">Register here</Link>
            </p>
        </div>
        </div>
    );
};

export default Login;
