import React, { useState } from 'react';
import logo from '../Images/food.png';
import admin from '../Images/Admin.png';
import axios from 'axios';

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [responseMessage, setResponseMessage] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    const handleLogin = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setResponseMessage(""); // รีเซ็ตข้อความก่อนเริ่มกระบวนการ
        try {
            const response = await axios.post(
                "http://localhost:5293/api/Auth/login",
                { email, password },
                {
                    headers: {
                        "Content-Type": "application/json",
                    },
                }
            );
            console.log("Success:", response.data);
            setResponseMessage("Login successful!"); // แสดงข้อความสำเร็จ
        } catch (error) {
            console.error("Error:", error.response?.data || error.message);
            setResponseMessage(
                error.response?.data?.Message || 
                "Unable to login. Please check your email and password."
            );
        } finally {
            setIsLoading(false); // ยกเลิกสถานะ Loading
        }
    };

    return (
        <div className="login-register-container">
            <div className="logo">
                <img src={logo} alt="App Logo" />
            </div>
            <div className="form-container">
                <img src={admin} className="avatar" alt="Admin Avatar" />
                <form onSubmit={handleLogin}>
                    <input
                        type="email"
                        placeholder="Email..."
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password..."
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <button type="submit" className="btn" disabled={isLoading}>
                        {isLoading ? "Processing..." : "Login"}
                    </button>
                </form>
                <div
                    className="response-message"
                    style={{
                        color: responseMessage === "Login successful!" ? "green" : "red",
                        marginTop: "10px",
                    }}
                >
                    {responseMessage}
                </div>
            </div>
        </div>
    );
};

export default Login;
