import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/SelectBranch.css";
import { FaTh, FaList } from "react-icons/fa";

export default function SelectBranch() {
    const [branches, setBranches] = useState([]);
    const [selectedBranch, setSelectedBranch] = useState(null);
    const [viewMode, setViewMode] = useState("grid"); // "grid" or "list"
    const navigate = useNavigate();

    useEffect(() => {
        const fetchBranches = async () => {
        try {
            const response = await axios.get("http://localhost:5293/api/Branch/all", {
            headers: { Authorization: `Bearer ${localStorage.getItem("authToken")}` },
            });
            setBranches(response.data);
        } catch (error) {
            console.error("Error fetching branches:", error);
        }
        };

        fetchBranches();
    }, []);

    const handleSelectBranch = (branchName) => {
        setSelectedBranch(branchName);
        navigate("/sale");
    };

    return (
        <div className="container">
        <h1 className="header">Select Department</h1>
        <div className="search-container">
            <input className="search-bar" type="text" placeholder="Search..." />
            <FaTh
            onClick={() => setViewMode("grid")}
            style={{ cursor: "pointer", color: viewMode === "grid" ? "#5995fd" : "#c0c0c0" }}
            />
            <FaList
            onClick={() => setViewMode("list")}
            style={{ cursor: "pointer", color: viewMode === "list" ? "#5995fd" : "#c0c0c0" }}
            />
        </div>
        <div className={viewMode === "grid" ? "branch-grid" : "branch-list"}>
            {branches.map((branch) => (
            <div
                key={branch.id}
                className={`branch-card ${selectedBranch === branch.name ? "active" : ""}`}
                onClick={() => handleSelectBranch(branch.name)}
            >
                <img
                src={branch.data.IconUrl || "https://via.placeholder.com/50"}
                alt={branch.data.Name}
                />
                <p>{branch.data.Name}</p>
            </div>
            ))}
        </div>
        <div className="buttons">
            <button onClick={() => navigate("/")}>Back</button>
            <button onClick={() => alert("Selected: " + selectedBranch)}>Check Department</button>
        </div>
        </div>
    );
}
