import React, { useState } from "react";
import logo from "../Images/food.png";
import admin from "../Images/Admin.png";
import axios from "axios";
import { toast } from "react-toastify";
import { Link, useNavigate } from "react-router-dom";

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setIsLoading(true);
    
        // ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö Validation
        if (!email || !password) {
            toast.error("Email and Password are required.");
            setIsLoading(false);
            return;
        }
    
        try {
            // ‡πÄ‡∏£‡∏µ‡∏¢‡∏Å API ‡∏û‡∏£‡πâ‡∏≠‡∏° Header
            const response = await axios.post(
                "http://localhost:5293/api/Auth/login",
                { email, password },
                {
                    headers: {
                        "x-posapp-header": process.env.REACT_APP_HEADER_SECRET_KEY,
                    },
                    
                    
                    
                }
            );
            console.log("Header Key (from env):", process.env.REACT_APP_HEADER_SECRET_KEY);
    
            // ‡πÅ‡∏™‡∏î‡∏á Token ‡πÉ‡∏ô Console (‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡πÇ‡∏´‡∏°‡∏î‡∏û‡∏±‡∏í‡∏ô‡∏≤)
            if (process.env.NODE_ENV === "development") {
                console.log("JWT Token:", response.data.token);
            }
    
            // ‡πÄ‡∏Å‡πá‡∏ö JWT Token ‡πÉ‡∏ô LocalStorage
            localStorage.setItem("authToken", response.data.token);
    
            // ‡πÅ‡∏à‡πâ‡∏á‡πÄ‡∏ï‡∏∑‡∏≠‡∏ô‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à
            toast.success("Login successful! üéâ");
    
            // ‡∏ô‡∏≥‡∏ó‡∏≤‡∏á‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ Dashboard ‡∏´‡∏£‡∏∑‡∏≠ Sale
            navigate("/select-branch");
        } catch (error) {
            console.error(error.response?.data || error.message);
    
            if (error.response?.status === 400) {
                toast.error("Invalid request. Please check your input.");
            } else if (error.response?.status === 401) {
                toast.error("Invalid login credentials. Please try again.");
            } else {
                toast.error(
                    error.response?.data?.message || "Unable to login. Please try again."
                );
            }
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="login-register-container">
        <div className="logo">
            <img src={logo} alt="App Logo" />
        </div>
        <div className="form-container">
            <img src={admin} className="avatar" alt="Admin Avatar" />
            <form onSubmit={handleLogin}>
            {/* ‡∏ä‡πà‡∏≠‡∏á Email */}
            <input
            type="text-form"
            placeholder="Email..."
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="form-input"
            />

            {/* ‡∏ä‡πà‡∏≠‡∏á Password */}
            <div className="input-group">
            <input
                type={showPassword ? "text" : "password"} // ‡∏ã‡πà‡∏≠‡∏ô‡∏´‡∏£‡∏∑‡∏≠‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô
                placeholder="Password..."
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className="form-input"
            />
            <button
                type="button"
                onClick={() => setShowPassword(!showPassword)} // ‡∏™‡∏•‡∏±‡∏ö‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞ Show/Hide
                className="show-password-btn"
            >
                {showPassword ? "Hide" : "Show"}
            </button>
            </div>
            <button type="submit" className="btn" disabled={isLoading}>
                {isLoading ? <div className="spinner"></div> : "Login"}
            </button>
            </form>
            <p>
            Don't have an account? <Link to="/register">Register here</Link>
            </p>
        </div>
        </div>
    );
};

export default Login;
