import React, { useState } from 'react';
import axios from 'axios';
import logo from '../Images/food.png';
import admin from '../Images/Admin.png';
import { toast } from 'react-toastify';
import { Link } from 'react-router-dom';

const Register = () => {
    const [fullName, setFullName] = useState("");
    const [userRole, setUserRole] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    const handleRegister = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        try {
            const response = await axios.post(
                "http://localhost:5293/api/Auth/register",
                {
                    firstName: fullName.split(" ")[0], // ‡πÅ‡∏¢‡∏Å Firstname
                    lastName: fullName.split(" ")[1] || "", // ‡πÅ‡∏¢‡∏Å Lastname ‡∏´‡∏£‡∏∑‡∏≠‡∏Å‡∏≥‡∏´‡∏ô‡∏î Default ‡πÄ‡∏õ‡πá‡∏ô‡∏Ñ‡πà‡∏≤‡∏ß‡πà‡∏≤‡∏á
                    email,
                    password,
                    role: userRole || "admin" // role ‡∏Ñ‡πà‡∏≤ Default ‡πÄ‡∏õ‡πá‡∏ô 'user'
                },
                {
                    headers: {
                        "Content-Type": "application/json",
                    },
                }
            );
            console.log("Success:", response.data);
            toast.success("Registration Successful! üéâ");
        } catch (error) {
            console.error("Error:", error.response?.data || error.message);
            toast.error(
                error.response?.data?.Message || "Unable to register. Please try again."
            );
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="login-register-container">
            <div className="logo">
                <img src={logo} alt="Logo" />
            </div>
            <div className="form-container">
                <img src={admin} className="avatar" alt="Admin Avatar" />
                <form onSubmit={handleRegister}>
                    <input
                        type="fullName"
                        placeholder="Full Name... (e.g. John Doe)"
                        value={fullName}
                        onChange={(e) => setFullName(e.target.value)}
                        required
                    />
                    {/* <select
                        value={userRole}
                        onChange={(e) => setUserRole(e.target.value)}
                        required
                    >
                        <option value="" disabled>Select Role</option>
                        <option value="user">User</option>
                        <option value="admin">Admin</option>
                    </select> */}
                    <input
                        type="email"
                        placeholder="Email Address..."
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password..."
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <button type="submit" className="btn" disabled={isLoading}>
                        {isLoading ? "Processing..." : "Register"}
                    </button>
                </form>
                <p>Already have an account? <Link to="/">Login here</Link></p>
            </div>
        </div>
    );
};

export default Register;
